function v = distort_sc(u, K, dist_params)
    % dist_params -- cell
        % {1}: [a0 a2 a3 a4]  % MappingCoefficients
        % {2}: [cx cy]        % DistortionCenter
        % {3}: [s1 s2; s3 s4] % Stretchmatrix

    a = dist_params{1};
    if any(abs(a)) > 0
        m = size(u,1);
        if (m == 2)
            v = PT.homogenize(u);
        else
            v = u;
        end
        v = K \ v;

        r = vecnorm(v(1:2,:),2,1);

        % % c(1) + c(2)*x + c(3)*x^2 + c(4)*x^3 + c(5)*x^4 = 0
        % coef = [r'.*a(1) -ones(size(r')) r'.*a(2:end)];
        % rts = solve_quartic(coef);
        % kv = rts(:,1)';

        for k=1:numel(r)
            rts2 = roots([a(4)*r(k), a(3)*r(k), a(2)*r(k), -1, a(1)*r(k)]);
            kv(k) = rts2(4);
            % METRICS.relerr(rts(k), kv(k));
        end
        v(1:2,:) = v(1:2,:) .* kv ./ r;
        
        v = K * v;     
        if (m == 2)
            v = v(1:2,:);
        end
    else
        v = u;
    end
end


